  --shadow-sm: 0px 2px 4px 0px hsl(0 0% 0% / 0.30), 0px 1px 2px -1px hsl(0 0% 0% / 0.25);
  --shadow: 0px 4px 6px 0px hsl(0 0% 0% / 0.35), 0px 2px 4px -1px hsl(0 0% 0% / 0.30);
  --shadow-md: 0px 6px 12px 0px hsl(0 0% 0% / 0.40), 0px 4px 6px -2px hsl(0 0% 0% / 0.35);
  --shadow-lg: 0px 10px 20px 0px hsl(0 0% 0% / 0.45), 0px 6px 8px -2px hsl(0 0% 0% / 0.40);
  --shadow-xl: 0px 16px 32px 0px hsl(0 0% 0% / 0.50), 0px 8px 12px -4px hsl(0 0% 0% / 0.45);
  --shadow-2xl: 0px 24px 48px 0px hsl(0 0% 0% / 0.55);

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
